"use client"

import React, { useState, useEffect } from "react"

interface LoginProps {
  onLogin: () => void
}

function Login({ onLogin }: LoginProps) {
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")
  const [animationPhase, setAnimationPhase] = useState(0)

  useEffect(() => {
    const interval = setInterval(() => {
      setAnimationPhase((prev) => (prev + 1) % 100)
    }, 100)
    return () => clearInterval(interval)
  }, [])

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault()
    const user = JSON.parse(localStorage.getItem("user") || "{}")
    if (user && user.username === username && user.password === password) {
      onLogin()
    } else {
      alert("Invalid credentials!")
    }
  }

  return (
    <div className="min-h-screen bg-black relative overflow-hidden flex items-center justify-center">
      {/* Simple animated background */}
      <div className="absolute inset-0">
        <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-cyan-500/10 rounded-full blur-3xl animate-pulse"></div>
        <div className="absolute bottom-1/4 right-1/4 w-80 h-80 bg-purple-500/10 rounded-full blur-3xl animate-pulse"></div>
        
        {/* Simple chart lines */}
        <svg className="absolute inset-0 w-full h-full opacity-20">
          <polyline
            fill="none"
            stroke="rgba(0,255,255,0.3)"
            strokeWidth="2"
            points={Array.from({ length: 20 }, (_, i) => 
              `${i * 50},${300 + Math.sin(i * 0.3 + animationPhase * 0.1) * 50}`
            ).join(" ")}
          />
        </svg>

        {/* TRADGOO logo */}
        <div className="absolute top-20 left-1/2 transform -translate-x-1/2">
          <h1 className="text-4xl font-bold bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
            TRADGOO
          </h1>
        </div>
      </div>

      {/* Login Form */}
      <div className="relative z-10 w-full max-w-md mx-4">
        <div className="bg-gray-900/80 backdrop-blur-sm border border-cyan-500/30 rounded-xl p-6 shadow-xl">
          <h2 className="text-2xl font-bold text-center mb-6 text-cyan-400">
            Login
          </h2>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label htmlFor="username" className="block text-sm text-cyan-300 mb-1">
                Username
              </label>
              <input
                name="username"
                id="username"
                type="text"
                className="w-full px-3 py-2 bg-black/50 border border-cyan-500/50 rounded text-white focus:outline-none focus:ring-1 focus:ring-cyan-400"
                required
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter username"
              />
            </div>

            <div>
              <label htmlFor="password" className="block text-sm text-cyan-300 mb-1">
                Password
              </label>
              <input
                name="password"
                id="password"
                type="password"
                className="w-full px-3 py-2 bg-black/50 border border-cyan-500/50 rounded text-white focus:outline-none focus:ring-1 focus:ring-cyan-400"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter password"
              />
            </div>

            <button
              type="submit"
              className="w-full py-2 bg-gradient-to-r from-cyan-500 to-purple-500 text-white font-semibold rounded hover:from-cyan-600 hover:to-purple-600 transition-all duration-300"
            >
              Login
            </button>
          </form>
        </div>
      </div>
    </div>
  )
}

export default Login
