"use client"

import type React from "react"
import { useState, useEffect } from "react"

// TRADGOO Background Component
function TradgooBackground() {
  const [animationPhase, setAnimationPhase] = useState(0)

  useEffect(() => {
    const interval = setInterval(() => {
      setAnimationPhase((prev) => (prev + 1) % 100)
    }, 100)
    return () => clearInterval(interval)
  }, [])

  // Generate random data points for charts
  const generateChartData = (points: number) => {
    return Array.from({ length: points }, (_, i) => ({
      x: i * 10,
      y: Math.sin(i * 0.3 + animationPhase * 0.1) * 20 + 50 + Math.random() * 10,
    }))
  }

  const chartData = generateChartData(20)
  const barHeights = Array.from({ length: 12 }, () => Math.random() * 80 + 20)

  return (
    <div className="fixed inset-0 bg-black overflow-hidden pointer-events-none">
      {/* Background gradient */}
      <div className="absolute inset-0 bg-gradient-to-br from-gray-900 via-black to-gray-800"></div>

      {/* Ambient lighting effects */}
      <div className="absolute top-1/4 left-1/4 w-96 h-96 bg-cyan-500/10 rounded-full blur-3xl animate-pulse"></div>
      <div className="absolute bottom-1/4 right-1/4 w-80 h-80 bg-blue-500/10 rounded-full blur-3xl animate-pulse delay-1000"></div>

      {/* Main container */}
      <div className="relative z-10 flex items-center justify-center h-full p-8">
        <div className="relative">
          {/* Central phone/device mockup */}
          <div className="relative mx-auto mb-8">
            <div className="w-64 h-96 bg-gradient-to-b from-gray-800 to-gray-900 rounded-3xl border border-gray-600 shadow-2xl transform perspective-1000 rotateY-12">
              {/* Enhanced phone screen with multiple charts */}
              <div className="m-4 h-80 bg-black rounded-2xl overflow-hidden relative">
                {/* Main trading chart */}
                <div className="absolute inset-2">
                  <svg className="w-full h-full" viewBox="0 0 200 150">
                    {/* Enhanced grid */}
                    <defs>
                      <pattern id="grid" width="10" height="10" patternUnits="userSpaceOnUse">
                        <path d="M 10 0 L 0 0 0 10" fill="none" stroke="rgba(0,255,255,0.1)" strokeWidth="0.3" />
                      </pattern>
                      <linearGradient id="gradient1" x1="0%" y1="0%" x2="100%" y2="0%">
                        <stop offset="0%" stopColor="#00ffff" />
                        <stop offset="50%" stopColor="#0080ff" />
                        <stop offset="100%" stopColor="#8000ff" />
                      </linearGradient>
                      <linearGradient id="volumeGradient" x1="0%" y1="0%" x2="0%" y2="100%">
                        <stop offset="0%" stopColor="rgba(0,255,255,0.8)" />
                        <stop offset="100%" stopColor="rgba(0,255,255,0.1)" />
                      </linearGradient>
                    </defs>

                    <rect width="100%" height="100%" fill="url(#grid)" />

                    {/* Candlestick chart */}
                    {Array.from({ length: 15 }).map((_, i) => {
                      const x = i * 12 + 10
                      const high = 30 + Math.random() * 40
                      const low = high - Math.random() * 20
                      const open = low + Math.random() * (high - low)
                      const close = low + Math.random() * (high - low)
                      const isGreen = close > open

                      return (
                        <g key={i}>
                          {/* Wick */}
                          <line
                            x1={x}
                            y1={high}
                            x2={x}
                            y2={low}
                            stroke={isGreen ? "#00ff88" : "#ff4444"}
                            strokeWidth="1"
                          />
                          {/* Body */}
                          <rect
                            x={x - 2}
                            y={Math.min(open, close)}
                            width="4"
                            height={Math.abs(close - open) || 1}
                            fill={isGreen ? "#00ff88" : "#ff4444"}
                            opacity="0.8"
                          />
                        </g>
                      )
                    })}

                    {/* Moving average line */}
                    <polyline
                      fill="none"
                      stroke="url(#gradient1)"
                      strokeWidth="1.5"
                      points={chartData.map((point) => `${point.x},${point.y}`).join(" ")}
                      className="drop-shadow-lg"
                    />

                    {/* Volume bars at bottom */}
                    {Array.from({ length: 15 }).map((_, i) => (
                      <rect
                        key={i}
                        x={i * 12 + 8}
                        y={130}
                        width="6"
                        height={Math.random() * 15 + 5}
                        fill="url(#volumeGradient)"
                      />
                    ))}

                    {/* Price indicators */}
                    <text x="5" y="15" fill="#00ffff" fontSize="6">
                      $45,234
                    </text>
                    <text x="5" y="25" fill="#00ff88" fontSize="5">
                      +2.34%
                    </text>
                  </svg>
                </div>
              </div>
            </div>
          </div>

          {/* TRADGOO Logo */}
          <div className="text-center mb-12">
            <h1 className="text-6xl font-bold tracking-wider">
              <span className="bg-gradient-to-r from-cyan-400 via-blue-400 to-purple-400 bg-clip-text text-transparent drop-shadow-2xl">
                TRADGOO
              </span>
            </h1>
            <div className="mt-4 h-1 w-32 mx-auto bg-gradient-to-r from-cyan-400 to-purple-400 rounded-full shadow-lg shadow-cyan-400/50"></div>
          </div>

          {/* 3D Bar Charts */}
          <div className="flex justify-center items-end space-x-2 mb-8">
            {barHeights.map((height, i) => (
              <div key={i} className="relative">
                <div
                  className="w-6 bg-gradient-to-t from-cyan-600 via-blue-500 to-purple-400 rounded-t-sm shadow-lg transform-gpu"
                  style={{
                    height: `${height + Math.sin(animationPhase * 0.1 + i) * 10}px`,
                    boxShadow: "0 0 20px rgba(0, 255, 255, 0.5)",
                    transform: "perspective(500px) rotateX(45deg)",
                  }}
                ></div>
                {/* Bar reflection */}
                <div
                  className="w-6 bg-gradient-to-b from-cyan-600/30 to-transparent rounded-b-sm transform-gpu"
                  style={{
                    height: `${(height + Math.sin(animationPhase * 0.1 + i) * 10) * 0.3}px`,
                    transform: "perspective(500px) rotateX(-45deg) scaleY(-1)",
                    transformOrigin: "top",
                  }}
                ></div>
              </div>
            ))}
          </div>

          {/* Floating data elements */}
          <div className="absolute -top-20 -left-20">
            {Array.from({ length: 8 }).map((_, i) => (
              <div
                key={i}
                className="absolute w-3 h-3 rounded-full animate-bounce"
                style={{
                  backgroundColor: ["#00ffff", "#ff00ff", "#ffff00", "#00ff00"][i % 4],
                  left: `${(i % 4) * 30}px`,
                  top: `${Math.floor(i / 4) * 40}px`,
                  animationDelay: `${i * 200}ms`,
                  boxShadow: `0 0 10px ${["#00ffff", "#ff00ff", "#ffff00", "#00ff00"][i % 4]}`,
                }}
              ></div>
            ))}
          </div>

          {/* Right side floating elements */}
          <div className="absolute -top-10 -right-32">
            {Array.from({ length: 6 }).map((_, i) => (
              <div
                key={i}
                className="absolute w-4 h-4 rounded-full animate-pulse"
                style={{
                  backgroundColor: ["#ff6b6b", "#4ecdc4", "#45b7d1", "#96ceb4", "#feca57", "#ff9ff3"][i],
                  left: `${(i % 3) * 25}px`,
                  top: `${Math.floor(i / 3) * 35}px`,
                  animationDelay: `${i * 300}ms`,
                  boxShadow: `0 0 15px ${["#ff6b6b", "#4ecdc4", "#45b7d1", "#96ceb4", "#feca57", "#ff9ff3"][i]}`,
                }}
              ></div>
            ))}
          </div>

          {/* Platform/base effect */}
          <div className="absolute -bottom-8 left-1/2 transform -translate-x-1/2 w-96 h-4 bg-gradient-to-r from-transparent via-cyan-500/20 to-transparent rounded-full blur-sm"></div>
        </div>
      </div>

      {/* Scanning line effect */}
      <div
        className="absolute left-0 w-full h-0.5 bg-gradient-to-r from-transparent via-cyan-400 to-transparent opacity-60"
        style={{
          top: `${50 + Math.sin(animationPhase * 0.05) * 30}%`,
          boxShadow: "0 0 20px #00ffff",
        }}
      ></div>

      {/* Corner decorative elements */}
      <div className="absolute top-8 left-8 w-16 h-16 border-l-2 border-t-2 border-cyan-400/50"></div>
      <div className="absolute top-8 right-8 w-16 h-16 border-r-2 border-t-2 border-cyan-400/50"></div>
      <div className="absolute bottom-8 left-8 w-16 h-16 border-l-2 border-b-2 border-cyan-400/50"></div>
      <div className="absolute bottom-8 right-8 w-16 h-16 border-r-2 border-b-2 border-cyan-400/50"></div>

      {/* Background large trading chart */}
      <div className="absolute top-0 left-0 w-full h-full opacity-20 pointer-events-none">
        <svg className="w-full h-full" viewBox="0 0 800 600">
          <defs>
            <linearGradient id="bgGradient" x1="0%" y1="0%" x2="100%" y2="100%">
              <stop offset="0%" stopColor="#00ffff" stopOpacity="0.3" />
              <stop offset="100%" stopColor="#8000ff" stopOpacity="0.1" />
            </linearGradient>
          </defs>

          {/* Large background chart */}
          <polyline
            fill="none"
            stroke="url(#bgGradient)"
            strokeWidth="3"
            points={Array.from(
              { length: 50 },
              (_, i) => `${i * 16},${300 + Math.sin(i * 0.2 + animationPhase * 0.05) * 100 + Math.random() * 50}`,
            ).join(" ")}
          />

          {/* Area fill */}
          <polygon
            fill="url(#bgGradient)"
            points={`0,600 ${Array.from(
              { length: 50 },
              (_, i) => `${i * 16},${300 + Math.sin(i * 0.2 + animationPhase * 0.05) * 100 + Math.random() * 50}`,
            ).join(" ")} 800,600`}
          />
        </svg>
      </div>

      {/* Side panel charts */}
      <div className="absolute right-8 top-1/4 space-y-6">
        {/* Circular progress charts */}
        {["BTC", "ETH", "SOL"].map((crypto, i) => (
          <div key={crypto} className="relative">
            <svg width="60" height="60" className="transform -rotate-90">
              <circle cx="30" cy="30" r="25" fill="none" stroke="rgba(0,255,255,0.2)" strokeWidth="3" />
              <circle
                cx="30"
                cy="30"
                r="25"
                fill="none"
                stroke={`hsl(${180 + i * 60}, 100%, 50%)`}
                strokeWidth="3"
                strokeDasharray={`${60 + i * 20 + Math.sin(animationPhase * 0.1) * 10} 157`}
                className="transition-all duration-300"
              />
            </svg>
            <div className="absolute inset-0 flex items-center justify-center">
              <span className="text-xs text-cyan-400 font-bold">{crypto}</span>
            </div>
          </div>
        ))}
      </div>

      {/* Left side mini charts */}
      <div className="absolute left-8 top-1/3 space-y-4">
        {Array.from({ length: 3 }).map((_, i) => (
          <div key={i} className="w-24 h-16 bg-black/50 rounded border border-cyan-500/30 p-2">
            <svg className="w-full h-full" viewBox="0 0 80 40">
              <polyline
                fill="none"
                stroke={`hsl(${i * 120}, 100%, 50%)`}
                strokeWidth="1"
                points={Array.from(
                  { length: 10 },
                  (_, j) => `${j * 8},${20 + Math.sin(j * 0.5 + i + animationPhase * 0.1) * 10}`,
                ).join(" ")}
              />
            </svg>
          </div>
        ))}
      </div>

      {/* Bottom ticker tape */}
      <div className="absolute bottom-16 left-0 right-0 h-8 bg-black/70 border-t border-cyan-500/30 overflow-hidden">
        <div
          className="flex items-center h-full text-cyan-400 text-sm whitespace-nowrap"
          style={{
            transform: `translateX(${-animationPhase * 2}px)`,
            animation: "marquee 20s linear infinite",
          }}
        >
          <span className="mx-8">BTC: $45,234 (+2.34%)</span>
          <span className="mx-8">ETH: $3,456 (-1.23%)</span>
          <span className="mx-8">SOL: $234 (+5.67%)</span>
          <span className="mx-8">ADA: $1.23 (+0.89%)</span>
          <span className="mx-8">DOT: $12.34 (-2.45%)</span>
        </div>
      </div>

      <style jsx>{`
        @keyframes marquee {
          0% { transform: translateX(100%); }
          100% { transform: translateX(-100%); }
        }
      `}</style>
    </div>
  )
}

// Login Component with TRADGOO Background
interface LoginProps {
  onLogin: () => void
}

function Login({ onLogin }: LoginProps) {
  const [username, setUsername] = useState("")
  const [password, setPassword] = useState("")

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault()
    const user = JSON.parse(localStorage.getItem("user") || "{}")
    // Check if the entered credentials match the stored ones
    if (user && user.username === username && user.password === password) {
      onLogin() // Call this on successful login
    } else {
      alert("Invalid credentials!") // Handle invalid login
    }
  }

  return (
    <div className="relative min-h-screen">
      {/* TRADGOO Background */}
      <TradgooBackground />

      {/* Login Form Overlay */}
      <div className="relative z-20 min-h-screen flex items-center justify-center">
        <div className="w-full max-w-md">
          {/* Glass morphism card for the login form */}
          <div className="bg-black/40 backdrop-blur-lg border border-cyan-500/30 rounded-2xl p-8 shadow-2xl">
            <h2 className="text-3xl font-bold text-center mb-8 bg-gradient-to-r from-cyan-400 to-purple-400 bg-clip-text text-transparent">
              Login to TRADGOO
            </h2>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label htmlFor="username" className="block text-sm font-medium text-cyan-300 mb-2">
                  Username
                </label>
                <input
                  name="username"
                  id="username"
                  type="text"
                  className="w-full px-4 py-3 bg-black/50 border border-cyan-500/50 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-cyan-400 focus:border-transparent transition-all duration-300"
                  required
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter your username"
                />
              </div>

              <div>
                <label htmlFor="password" className="block text-sm font-medium text-cyan-300 mb-2">
                  Password
                </label>
                <input
                  name="password"
                  id="password"
                  type="password"
                  className="w-full px-4 py-3 bg-black/50 border border-cyan-500/50 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-cyan-400 focus:border-transparent transition-all duration-300"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                />
              </div>

              <button
                type="submit"
                className="w-full py-3 bg-gradient-to-r from-cyan-500 to-purple-500 text-white font-semibold rounded-lg hover:from-cyan-600 hover:to-purple-600 focus:outline-none focus:ring-2 focus:ring-cyan-400 focus:ring-offset-2 focus:ring-offset-black transition-all duration-300 transform hover:scale-105 shadow-lg shadow-cyan-500/25"
              >
                Login
              </button>
            </form>

            {/* Additional styling elements */}
            <div className="mt-6 text-center">
              <div className="h-px bg-gradient-to-r from-transparent via-cyan-500/50 to-transparent"></div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Login
