import React, { useState } from "react";

function TourismPage() {
  const [activeDetail, setActiveDetail] = useState(null);

  const openDetail = (placeId) => setActiveDetail(placeId);
  const closeDetail = () => setActiveDetail(null);

  const places = [
    {
      id: "taj",
      name: "Taj Mahal",
      location: "Agra, India",
      weather: "Hot summers, mild winters",
      bestSeason: "October – March",
      disasters: "2019 floods nearby, 2021 Covid restrictions",
      img: "https://images.unsplash.com/photo-1505761671935-60b3a7427bad?auto=format&fit=crop&w=900&q=80",
      imgLarge:
        "https://images.unsplash.com/photo-1505761671935-60b3a7427bad?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Absolutely breathtaking.", "⭐ 4 – Crowded but worth it."],
      tagline: "A symbol of love, must-visit once in a lifetime.",
    },
    {
      id: "jaipur",
      name: "Jaipur City Palace",
      location: "Jaipur, Rajasthan, India",
      weather: "Hot summers, pleasant winters",
      bestSeason: "November – February",
      disasters: "2020 locust attack, 2021 pandemic",
      img: "https://images.unsplash.com/photo-1609940194206-8976f5f85c42?auto=format&fit=crop&w=900&q=80",
      imgLarge:
        "https://images.unsplash.com/photo-1609940194206-8976f5f85c42?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Felt like royalty.", "⭐ 4 – Too many tourists."],
      tagline: "Royal vibes and amazing architecture.",
    },
    {
      id: "goa",
      name: "Goa Beaches",
      location: "Goa, India",
      weather: "Tropical, humid",
      bestSeason: "November – February",
      disasters: "2018 cyclone, 2021 Covid impact",
      img: "https://images.unsplash.com/photo-1548013146-72479768bada?auto=format&fit=crop&w=900&q=80",
      imgLarge:
        "https://images.unsplash.com/photo-1548013146-72479768bada?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Amazing nightlife.", "⭐ 4 – Beaches are great, but crowded."],
      tagline: "Perfect for sunsets, parties, and relaxation.",
    },
    // Add more places up to 25 similarly...
  ];

  return (
    <main className="container my-5">
      <h1 className="text-center mb-5">Explore India</h1>

      {/* Places Grid */}
      <div className="row g-4">
        {places.map((place, index) => (
          <div key={place.id} className="col-md-4">
            <div
              className="card shadow-sm place-card"
              style={{ cursor: "pointer" }}
              onClick={() => openDetail(place.id)}
            >
              <img src={place.img} className="card-img-top" alt={place.name} />
              <div className="card-body">
                <h5 className="card-title">{place.name}</h5>
                <p className="card-text">
                  {place.tagline} <br /> {place.reviews[0]}
                </p>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Left/Right Featured Sections */}
      {places.map((place, index) => {
        const isLeft = index % 2 === 0;
        return (
          <div key={place.id} className="container mt-5">
            <div className="row align-items-center">
              {isLeft && (
                <div className="col-6">
                  <img src={place.img} alt={place.name} className="img-fluid rounded shadow" />
                </div>
              )}
              <div className="col-6 p-5 mt-5">
                <h2>{place.name}</h2>
                <p>{place.tagline}</p>
                <p>
                  <strong>Location:</strong> {place.location} <br />
                  <strong>Weather:</strong> {place.weather} <br />
                  <strong>Best Season:</strong> {place.bestSeason} <br />
                  <strong>Disasters:</strong> {place.disasters}
                </p>
                <a
                  href={`/place/${place.id}`}
                  className="btn btn-primary"
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  Learn More
                </a>
              </div>
              {!isLeft && (
                <div className="col-6">
                  <img src={place.img} alt={place.name} className="img-fluid rounded shadow" />
                </div>
              )}
            </div>
          </div>
        );
      })}

      {/* Modal */}
      {activeDetail && (
        <div
          className="modal-overlay"
          onClick={closeDetail}
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0,0,0,0.6)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 1000,
          }}
        >
          <div
            className="modal-content bg-white p-5 rounded"
            onClick={(e) => e.stopPropagation()}
            style={{ maxWidth: "800px", width: "90%", position: "relative" }}
          >
            <button
              className="btn btn-danger"
              onClick={closeDetail}
              style={{ fontSize: "1.5em", padding: "10px 20px", position: "absolute", top: "10px", right: "10px" }}
            >
              ✖ Close
            </button>
            <img
              src={places.find((p) => p.id === activeDetail).imgLarge}
              className="img-fluid rounded mb-3"
              alt={places.find((p) => p.id === activeDetail).name}
            />
            <h2>{places.find((p) => p.id === activeDetail).name}</h2>
            <p>
              <strong>Location:</strong>{" "}
              {places.find((p) => p.id === activeDetail).location}
            </p>
            <p>
              <strong>Weather:</strong>{" "}
              {places.find((p) => p.id === activeDetail).weather}
            </p>
            <p>
              <strong>Best Season:</strong>{" "}
              {places.find((p) => p.id === activeDetail).bestSeason}
            </p>
            <p>
              <strong>Last Disasters:</strong>{" "}
              {places.find((p) => p.id === activeDetail).disasters}
            </p>
            <ul>
              {places
                .find((p) => p.id === activeDetail)
                .reviews.map((r, i) => (
                  <li key={i}>{r}</li>
                ))}
            </ul>
          </div>
        </div>
      )}
    </main>
  );
}

export default TourismPage;
