import React from 'react';

function Hero() {
  return ( 
    <div className='container p-5 mb-5' style={{ backgroundColor: '#001f3f', minHeight: '100vh', color: 'white' }}>
      <div className='row text-center'>
        <img src='media/images/homeHero.png' alt='Hero Images' className='mb-5' style={{ filter: 'brightness(0.9)' }}/>
        <h1 className='mt-5'>
          Invest in everything
        </h1>
        <p>
          online platform to invest in stocks, derivatives, mutual funds, and more
        </p>
        <button className='p-2 btn btn-light fs-5 mb-5' style={{ width: "25%", margin: '0 auto' }}>
          Signup
        </button>
      </div>
    </div>
  );
}

export default Hero;
