import React from "react";

function TourismSection({
  imageURL,
  placeName,
  location,
  weather,
  bestSeason,
  disasters,
  tagline,
  reviews = [],
  learnMore,
}) {
  return (
    <div className="container mt-5">
      <div className="row align-items-center">
        {/* Left: Place Image */}
        <div className="col-lg-6 col-md-12 mb-4 mb-lg-0 text-center">
          <img
            src={imageURL}
            alt={placeName}
            className="img-fluid rounded shadow-sm"
            style={{ maxHeight: "400px", objectFit: "cover" }}
          />
        </div>

        {/* Right: Place Details */}
        <div className="col-lg-6 col-md-12 p-4">
          <h1 className="fw-bold">{placeName}</h1>
          <p className="text-muted fs-5">{tagline}</p>

          <ul className="list-unstyled">
            <li><strong>üìç Location:</strong> {location}</li>
            <li><strong>üå¶ Weather:</strong> {weather}</li>
            <li><strong>üóì Best Season:</strong> {bestSeason}</li>
            <li><strong>‚ö† Last Disasters:</strong> {disasters}</li>
          </ul>

          {reviews.length > 0 && (
            <div className="mt-3">
              <h5>User Reviews:</h5>
              <ul>
                {reviews.map((r, i) => (
                  <li key={i}>{r}</li>
                ))}
              </ul>
            </div>
          )}

          {learnMore && (
            <div className="mt-4">
              <a
                href={learnMore}
                className="btn btn-primary"
                target="_blank"
                rel="noopener noreferrer"
              >
                Learn More <i className="fa fa-arrow-right ms-1" aria-hidden="true"></i>
              </a>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

// ‚úÖ Example usage
function App() {
  return (
    <div>
      <TourismSection
        imageURL="https://images.unsplash.com/photo-1505761671935-60b3a7427bad"
        placeName="Taj Mahal"
        location="Agra, India"
        weather="Hot summers, mild winters"
        bestSeason="October ‚Äì March"
        disasters="2019 floods nearby, 2021 Covid restrictions"
        tagline="A symbol of love, must-visit once in a lifetime."
        reviews={[
          "‚≠ê 5 ‚Äì Absolutely breathtaking.",
          "‚≠ê 4 ‚Äì Crowded but worth it.",
        ]}
        learnMore="https://en.wikipedia.org/wiki/Taj_Mahal"
      />
    </div>
  );
}

export default App;
