import React, { useState } from 'react';

const LoginPage = () => {
  const [isFlipped, setIsFlipped] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const handleInputChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isFlipped) {
      alert(`Signing up as:\nName: ${formData.name}\nEmail: ${formData.email}\nPassword: ${formData.password}`);
    } else {
      alert(`Logging in with:\nEmail: ${formData.email}\nPassword: ${formData.password}`);
    }
  };

  const toggleFlip = () => {
    setIsFlipped((prev) => !prev);
    setFormData({ name: '', email: '', password: '' });
  };

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Poppins:wght@300;600&display=swap');

        * {
          box-sizing: border-box;
        }
        body {
          margin: 0;
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          background: linear-gradient(135deg, #0f2027, #203a43, #2c5364);
          font-family: 'Poppins', sans-serif;
          color: #fff;
        }
        .card-container {
          perspective: 1000px;
          width: 360px;
          height: 420px;
        }
        .card {
          width: 100%;
          height: 100%;
          position: relative;
          transition: transform 0.8s cubic-bezier(0.4, 0.2, 0.2, 1);
          transform-style: preserve-3d;
        }
        .card.flipped {
          transform: rotateY(180deg);
        }
        .card-face {
          position: absolute;
          width: 100%;
          height: 100%;
          background: #1c1f26;
          border-radius: 20px;
          box-shadow: 0 15px 25px rgba(0,0,0,0.6);
          padding: 40px 30px;
          backface-visibility: hidden;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }
        .card-face.front {
          /* front side */
        }
        .card-face.back {
          transform: rotateY(180deg);
        }
        h2 {
          margin: 0 0 30px;
          font-weight: 600;
          letter-spacing: 2px;
          font-size: 28px;
        }
        form {
          width: 100%;
        }
        input {
          width: 100%;
          padding: 12px 15px;
          margin-bottom: 25px;
          border-radius: 12px;
          border: none;
          background: #2e3340;
          color: #ddd;
          font-weight: 300;
          font-size: 16px;
          transition: box-shadow 0.3s ease, background-color 0.3s ease;
        }
        input::placeholder {
          color: #888;
        }
        input:focus {
          outline: none;
          background: #3b4150;
          box-shadow: 0 0 8px #38f9d7;
          color: #fff;
        }
        button {
          width: 100%;
          padding: 12px;
          border-radius: 12px;
          border: none;
          background: #38f9d7;
          color: #1c1f26;
          font-weight: 600;
          font-size: 18px;
          cursor: pointer;
          transition: background 0.3s ease;
        }
        button:hover {
          background: #2bc7b8;
        }
        .toggle-section {
          margin-top: 20px;
          text-align: center;
          font-size: 14px;
          color: #aaa;
          cursor: pointer;
          user-select: none;
        }
        .toggle-section:hover {
          color: #38f9d7;
          text-decoration: underline;
        }
        .toggle-section:focus {
          outline: 2px dashed #38f9d7;
          outline-offset: 4px;
        }
      `}</style>

      <div className="card-container" aria-live="polite">
        <div className={`card ${isFlipped ? 'flipped' : ''}`}>

          {/* Login Form */}
          <div className="card-face front" aria-hidden={isFlipped}>
            <h2>Welcome Back</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
                autoComplete="username"
                aria-label="Email"
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
                autoComplete="current-password"
                aria-label="Password"
              />
              <button type="submit">Login</button>
            </form>
            <div
              className="toggle-section"
              role="button"
              tabIndex={0}
              onClick={toggleFlip}
              onKeyDown={e => {
                if (e.key === 'Enter' || e.key === ' ') {
                  e.preventDefault();
                  toggleFlip();
                }
              }}
              aria-pressed={isFlipped}
            >
              Don't have an account? Sign Up
            </div>
          </div>

          {/* Signup Form */}
          <div className="card-face back" aria-hidden={!isFlipped}>
            <h2>Create Account</h2>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={formData.name}
                onChange={handleInputChange}
                required
                autoComplete="name"
                aria-label="Full Name"
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleInputChange}
                required
                autoComplete="email"
                aria-label="Email"
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleInputChange}
                required
                autoComplete="new-password"
                aria-label="Password"
              />
              <button type="submit">Sign Up</button>
            </form>
            <div
              className="toggle-section"
              role="button"
              tabIndex={0}
              onClick={toggleFlip}
              onKeyDown={e => {
                if (e.key === 'Enter' || e.key === ' ') {
                  e.preventDefault();
                  toggleFlip();
                }
              }}
              aria-pressed={!isFlipped}
            >
              Already have an account? Login
            </div>
          </div>

        </div>
      </div>
    </>
  );
};

export default LoginPage;
