import React from 'react';

// Single Support Topic Component
function SupportTopic({ icon, title, links }) {
  return (
    <div className="col-4 p-5 mt-2 mb-2">
      <h4>
        <i className={`fa ${icon}`} aria-hidden="true"></i> {title}
      </h4>
      {links.map((link, index) => (
        <a
          key={index}
          href={link.href}
          style={{ textDecoration: 'none', lineHeight: '2.5' }}
        >
          {link.text}
        </a>
      ))}
    </div>
  );
}

// Main Support Page
function CreateTicket() {
  const supportTopics = [
    {
      icon: 'fa-plus-circle',
      title: 'Account Opening',
      links: [
        { text: 'Online Account Opening', href: '#' },
        { text: 'Offline Account Opening', href: '#' },
        { text: 'Company, Partnership and HUF Account', href: '#' },
        { text: 'NRI Account Opening', href: '#' },
        { text: 'Charges at TRADGOO', href: '#' },
        { text: 'TRADGOO IDFC FIRST Bank 3-in-1 Account', href: '#' },
        { text: 'Getting Started', href: '#' },
      ],
    },
    {
      icon: 'fa-user',
      title: 'Your TRADGOO Account',
      links: [
        { text: 'Profile & Settings', href: '#' },
        { text: 'Verify Documents', href: '#' },
        { text: 'Update KYC', href: '#' },
        { text: 'Account Statements', href: '#' },
      ],
    },
    {
      icon: 'fa-bar-chart',
      title: 'Your Account',
      links: [
        { text: 'Portfolio Overview', href: '#' },
        { text: 'Trade History', href: '#' },
        { text: 'Investment Analytics', href: '#' },
      ],
    },
    {
      icon: 'fa-xing-square',
      title: 'Funds',
      links: [
        { text: 'Add Funds', href: '#' },
        { text: 'Withdraw Funds', href: '#' },
        { text: 'Fund Transfers', href: '#' },
      ],
    },
    {
      icon: 'fa-circle-o-notch',
      title: 'Console',
      links: [
        { text: 'Dashboard', href: '#' },
        { text: 'Reports', href: '#' },
        { text: 'Alerts & Notifications', href: '#' },
      ],
    },
    {
      icon: 'fa-circle-thin',
      title: 'Coin',
      links: [
        { text: 'Mutual Funds', href: '#' },
        { text: 'SIP Setup', href: '#' },
        { text: 'Transaction History', href: '#' },
      ],
    },
  ];

  return (
    <div className="container">
      <div className="row p-5 mt-5 mb-5">
        <h1 className="fs-2 mb-4">
          To create a ticket, select a relevant topic
        </h1>
        {supportTopics.map((topic, index) => (
          <SupportTopic
            key={index}
            icon={topic.icon}
            title={topic.title}
            links={topic.links}
          />
        ))}
      </div>
    </div>
  );
}

export default CreateTicket;
