// export default Signup;


import React, { useState, useEffect } from 'react';

const containerStyle = {
  maxWidth: 360,
  margin: '40px auto',
  background: 'rgba(255, 255, 255, 0.1)',
  borderRadius: 12,
  boxShadow: '0 16px 40px rgba(0,0,0,0.3)',
  color: '#fff',
  fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  overflow: 'hidden',
};

const buttonGroupStyle = {
  display: 'flex',
  backgroundColor: 'rgba(0,0,0,0.2)',
  borderRadius: '12px 12px 0 0',
};

const tabBtnStyle = {
  flex: 1,
  background: 'none',
  border: 'none',
  padding: '16px 0',
  fontSize: 18,
  color: '#ccc',
  fontWeight: 600,
  cursor: 'pointer',
  userSelect: 'none',
  transition: 'color 0.3s ease, background-color 0.3s ease',
};

const tabBtnActiveStyle = {
  color: '#764ba2',
  backgroundColor: '#fff',
  fontWeight: '700',
  borderRadius: '12px 12px 0 0',
  boxShadow: '0 -3px 12px #764ba2aa',
};

const formStyle = {
  background: '#fff',
  color: '#333',
  padding: 30,
  borderRadius: '0 0 12px 12px',
  display: 'flex',
  flexDirection: 'column',
  minHeight: 300,
  justifyContent: 'center',
  boxSizing: 'border-box',
};

const labelStyle = {
  marginBottom: 6,
  fontSize: 14,
  fontWeight: 600,
};

const inputStyle = {
  padding: '12px 14px',
  marginBottom: 20,
  border: '2px solid #ddd',
  borderRadius: 8,
  fontSize: 16,
  outline: 'none',
};

const inputFocusStyle = {
  borderColor: '#764ba2',
};

const submitBtnStyle = {
  backgroundColor: '#764ba2',
  border: 'none',
  padding: 14,
  borderRadius: 8,
  color: 'white',
  fontSize: 18,
  cursor: 'pointer',
  fontWeight: 700,
  transition: 'background-color 0.3s ease',
};

const submitBtnHoverStyle = {
  backgroundColor: '#5a368f',
};

const messageStyle = {
  marginTop: 12,
  fontWeight: 600,
  fontSize: 14,
  textAlign: 'center',
  minHeight: 24,
};

const successMessageStyle = {
  color: '#30a14e',
};

const errorMessageStyle = {
  color: '#d63447',
};

function SignupApp() {
  const [activeTab, setActiveTab] = useState('signup'); // 'signup' or 'login'

  // Signup form states
  const [signupUsername, setSignupUsername] = useState('');
  const [signupPassword, setSignupPassword] = useState('');
  const [signupMessage, setSignupMessage] = useState(null);

  // Login form states
  const [loginUsername, setLoginUsername] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [loginMessage, setLoginMessage] = useState(null);

  // Hover button effect for submit buttons
  const [signupBtnHover, setSignupBtnHover] = useState(false);
  const [loginBtnHover, setLoginBtnHover] = useState(false);

  // Utility function to get users from localStorage
  function getUsers() {
    const users = localStorage.getItem('users');
    return users ? JSON.parse(users) : {};
  }

  // Utility function to save user
  function saveUser(username, password) {
    const users = getUsers();
    users[username] = password;
    localStorage.setItem('users', JSON.stringify(users));
  }

  // Signup handler
  function handleSignup(e) {
    e.preventDefault();
    setSignupMessage(null);
    const username = signupUsername.trim();
    const password = signupPassword;

    if (username.length < 3) {
      setSignupMessage({ type: 'error', text: 'Username must be at least 3 characters.' });
      return;
    }
    if (password.length < 6) {
      setSignupMessage({ type: 'error', text: 'Password must be at least 6 characters.' });
      return;
    }

    const users = getUsers();
    if (users[username]) {
      setSignupMessage({ type: 'error', text: 'Username already exists. Please login.' });
      return;
    }

    saveUser(username, password);
    setSignupMessage({ type: 'success', text: 'Registration successful! You can now log in.' });
    // Clear signup fields
    setSignupUsername('');
    setSignupPassword('');
    // Switch to login tab after short delay
    setTimeout(() => setActiveTab('login'), 1500);
  }

  // Login handler
  function handleLogin(e) {
    e.preventDefault();
    setLoginMessage(null);
    const username = loginUsername.trim();
    const password = loginPassword;

    if (!username || !password) {
      setLoginMessage({ type: 'error', text: 'Please fill in both fields.' });
      return;
    }

    const users = getUsers();
    if (!users[username]) {
      setLoginMessage({ type: 'error', text: 'No account found with this username.' });
      return;
    }
    if (users[username] !== password) {
      setLoginMessage({ type: 'error', text: 'Incorrect password.' });
      return;
    }

    setLoginMessage({ type: 'success', text: `Welcome back, ${username}!` });
    // Clear login fields on success after delay
    setTimeout(() => {
      setLoginUsername('');
      setLoginPassword('');
      setLoginMessage(null);
      // Further action could be added here after login success
    }, 2500);
  }

  return (
    <div style={containerStyle} role="main" aria-label="Signup and Login Forms">
      <div role="tablist" style={buttonGroupStyle}>
        <button
          type="button"
          onClick={() => setActiveTab('signup')}
          aria-selected={activeTab === 'signup'}
          aria-controls="signup-form"
          role="tab"
          style={{
            ...tabBtnStyle,
            ...(activeTab === 'signup' ? tabBtnActiveStyle : {}),
          }}
        >
          Sign Up
        </button>
        <button
          type="button"
          onClick={() => setActiveTab('login')}
          aria-selected={activeTab === 'login'}
          aria-controls="login-form"
          role="tab"
          style={{
            ...tabBtnStyle,
            ...(activeTab === 'login' ? tabBtnActiveStyle : {}),
          }}
        >
          Log In
        </button>
      </div>

      <form
        id="signup-form"
        onSubmit={handleSignup}
        role="tabpanel"
        aria-labelledby="signup-tab"
        aria-hidden={activeTab !== 'signup'}
        style={{ ...formStyle, display: activeTab === 'signup' ? 'flex' : 'none' }}
        noValidate
      >
        <label htmlFor="signup-username" style={labelStyle}>
          Username
        </label>
        <input
          type="text"
          id="signup-username"
          name="signup-username"
          placeholder="Choose your username"
          value={signupUsername}
          onChange={(e) => setSignupUsername(e.target.value)}
          style={inputStyle}
          minLength={3}
          autoComplete="username"
          required
        />
        <label htmlFor="signup-password" style={labelStyle}>
          Password
        </label>
        <input
          type="password"
          id="signup-password"
          name="signup-password"
          placeholder="Create a password"
          value={signupPassword}
          onChange={(e) => setSignupPassword(e.target.value)}
          style={inputStyle}
          minLength={6}
          autoComplete="new-password"
          required
        />
        <button
          type="submit"
          style={{
            ...submitBtnStyle,
            ...(signupBtnHover ? submitBtnHoverStyle : {}),
          }}
          onMouseEnter={() => setSignupBtnHover(true)}
          onMouseLeave={() => setSignupBtnHover(false)}
        >
          Register
        </button>
        <div
          role="alert"
          aria-live="polite"
          style={{
            ...messageStyle,
            ...(signupMessage?.type === 'success' ? successMessageStyle : {}),
            ...(signupMessage?.type === 'error' ? errorMessageStyle : {}),
          }}
        >
          {signupMessage?.text}
        </div>
      </form>

      <form
        id="login-form"
        onSubmit={handleLogin}
        role="tabpanel"
        aria-labelledby="login-tab"
        aria-hidden={activeTab !== 'login'}
        style={{ ...formStyle, display: activeTab === 'login' ? 'flex' : 'none' }}
        noValidate
      >
        <label htmlFor="login-username" style={labelStyle}>
          Username
        </label>
        <input
          type="text"
          id="login-username"
          name="login-username"
          placeholder="Enter your username"
          value={loginUsername}
          onChange={(e) => setLoginUsername(e.target.value)}
          style={inputStyle}
          autoComplete="username"
          required
        />
        <label htmlFor="login-password" style={labelStyle}>
          Password
        </label>
        <input
          type="password"
          id="login-password"
          name="login-password"
          placeholder="Enter your password"
          value={loginPassword}
          onChange={(e) => setLoginPassword(e.target.value)}
          style={inputStyle}
          autoComplete="current-password"
          required
        />
        <button
          type="submit"
          style={{
            ...submitBtnStyle,
            ...(loginBtnHover ? submitBtnHoverStyle : {}),
          }}
          onMouseEnter={() => setLoginBtnHover(true)}
          onMouseLeave={() => setLoginBtnHover(false)}
        >
          Log In
        </button>
        <div
          role="alert"
          aria-live="polite"
          style={{
            ...messageStyle,
            ...(loginMessage?.type === 'success' ? successMessageStyle : {}),
            ...(loginMessage?.type === 'error' ? errorMessageStyle : {}),
          }}
        >
          {loginMessage?.text}
        </div>
      </form>
    </div>
  );
}

export default SignupApp;
