// export default Hero;





import React, { useState } from "react";

function Hero() {
  const [activeDetail, setActiveDetail] = useState(null);

  const openDetail = (id) => setActiveDetail(id);
  const closeDetail = () => setActiveDetail(null);

  return (
    <div>
      {/* Inline CSS for this component */}
      <style>{`
        .modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: rgba(0,0,0,0.7);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 1050;
        }
        .modal-content {
          background: #fff;
          padding: 20px;
          border-radius: 12px;
          max-width: 700px;
          width: 90%;
          max-height: 90%;
          overflow-y: auto;
        }
        .place-card:hover {
          transform: translateY(-4px);
          transition: transform 0.2s;
        }
      `}</style>

      {/* Header */}
      <header className="d-flex justify-content-between align-items-center p-3 shadow bg-white">
        <img
          src="logo.png"
          alt="Smart Tourist Safety Monitoring logo"
          height="50"
        />
        <nav>
          <a href="#places" className="mx-3 text-dark">
            Places
          </a>
          {/* <a href="#features" className="mx-3 text-dark">
            Features
          </a>
          <a href="#contact" className="mx-3 text-dark">
            Contact
          </a> */}
        </nav>
      </header>

      {/* Hero Banner */}
      <section
        className="d-flex align-items-center justify-content-center text-white text-center"
        style={{
          backgroundImage:
            "url('https://images.unsplash.com/photo-1507525428034-b723cf961d3e?auto=format&fit=crop&w=1600&q=80')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "340px",
        }}
      >
        <h1 className="p-3 rounded" style={{ background: "rgba(0,0,0,0.5)" }}>
          Travel Safe, Explore Freely
        </h1>
      </section>

      {/* Places */}
      <main className="container my-5">
        <h2 id="places" className="mb-4">
          Popular Tourist Places
        </h2>
        <div className="row g-4">
          {/* Taj Mahal */}
          <div className="col-md-4">
            <div
              className="card shadow-sm place-card"
              onClick={() => openDetail("taj")}
              style={{ cursor: "pointer" }}
            >
              <img
                src="https://images.unsplash.com/photo-1505761671935-60b3a7427bad?auto=format&fit=crop&w=900&q=80"
                className="card-img-top"
                alt="Taj Mahal"
              />
              <div className="card-body">
                <h5 className="card-title">Taj Mahal</h5>
                <p className="card-text">
                  ⭐ 4.8 | “A symbol of love, must-visit once in a lifetime.”
                </p>
              </div>
            </div>
          </div>

          {/* Jaipur */}
          <div className="col-md-4">
            <div
              className="card shadow-sm place-card"
              onClick={() => openDetail("jaipur")}
              style={{ cursor: "pointer" }}
            >
              <img
                src="https://images.unsplash.com/photo-1609940194206-8976f5f85c42?auto=format&fit=crop&w=900&q=80"
                className="card-img-top"
                alt="Jaipur Palace"
              />
              <div className="card-body">
                <h5 className="card-title">Jaipur City Palace</h5>
                <p className="card-text">
                  ⭐ 4.6 | “Royal vibes and amazing architecture.”
                </p>
              </div>
            </div>
          </div>

          {/* Goa */}
          <div className="col-md-4">
            <div
              className="card shadow-sm place-card"
              onClick={() => openDetail("goa")}
              style={{ cursor: "pointer" }}
            >
              <img
                src="https://images.unsplash.com/photo-1548013146-72479768bada?auto=format&fit=crop&w=900&q=80"
                className="card-img-top"
                alt="Goa Beach"
              />
              <div className="card-body">
                <h5 className="card-title">Goa Beaches</h5>
                <p className="card-text">
                  ⭐ 4.7 | “Perfect for sunsets, parties, and relaxation.”
                </p>
              </div>
            </div>
          </div>
        </div>
      </main>

      {/* Detail Modals */}
      {activeDetail === "taj" && (
        <div className="modal-overlay" onClick={closeDetail}>
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
          >
            <button className="btn btn-danger float-end" onClick={closeDetail}>
              Close ✖
            </button>
            <img
              src="https://images.unsplash.com/photo-1505761671935-60b3a7427bad?auto=format&fit=crop&w=1600&q=80"
              className="img-fluid rounded mb-3"
              alt="Taj Mahal"
            />
            <h2>Taj Mahal</h2>
            <p>
              <strong>Location:</strong> Agra, India
            </p>
            <p>
              <strong>Conditions:</strong> Open daily (except Fridays). Entry
              ticket required.
            </p>
            <ul>
              <li>⭐ 5 – “Absolutely breathtaking.”</li>
              <li>⭐ 4 – “Crowded but worth it.”</li>
            </ul>
          </div>
        </div>
      )}

      {activeDetail === "jaipur" && (
        <div className="modal-overlay" onClick={closeDetail}>
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
          >
            <button className="btn btn-danger float-end" onClick={closeDetail}>
              Close ✖
            </button>
            <img
              src="https://images.unsplash.com/photo-1609940194206-8976f5f85c42?auto=format&fit=crop&w=1600&q=80"
              className="img-fluid rounded mb-3"
              alt="Jaipur"
            />
            <h2>Jaipur City Palace</h2>
            <p>
              <strong>Location:</strong> Jaipur, India
            </p>
            <p>
              <strong>Conditions:</strong> Open all week. Guided tours
              recommended.
            </p>
          </div>
        </div>
      )}

      {activeDetail === "goa" && (
        <div className="modal-overlay" onClick={closeDetail}>
          <div
            className="modal-content"
            onClick={(e) => e.stopPropagation()}
          >
            <button className="btn btn-danger float-end" onClick={closeDetail}>
              Close ✖
            </button>
            <img
              src="https://images.unsplash.com/photo-1548013146-72479768bada?auto=format&fit=crop&w=1600&q=80"
              className="img-fluid rounded mb-3"
              alt="Goa"
            />
            <h2>Goa Beaches</h2>
            <p>
              <strong>Location:</strong> Goa, India
            </p>
            <p>
              <strong>Conditions:</strong> Best season: November–February.
            </p>
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="text-center py-4 bg-light mt-5">
        <p>© 2025 Smart Tourist Safety Monitoring</p>
        <p>
          <a href="#privacy">Privacy</a> | <a href="#terms">Terms</a>
        </p>
      </footer>
    </div>
  );
}

export default Hero;
