import React, { useState } from 'react';

const SignupLogin = () => {
  const [isLogin, setIsLogin] = useState(false);
  const [form, setForm] = useState({ email: '', password: '' });
  const [user, setUser] = useState(null); // To store signed up user
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSignup = () => {
    if (!form.email || !form.password) {
      setMessage('Please fill in all fields.');
      return;
    }
    setUser({ email: form.email, password: form.password });
    setForm({ email: '', password: '' });
    setIsLogin(true);
    setMessage('Signup successful! Please log in.');
  };

  const handleLogin = () => {
    if (!form.email || !form.password) {
      setMessage('Please fill in all fields.');
      return;
    }
    if (!user) {
      setMessage('No user found. Please sign up first.');
      return;
    }
    if (form.email === user.email && form.password === user.password) {
      setMessage('Login successful!');
    } else {
      setMessage('Invalid credentials.');
    }
  };

  return (
    <div style={{ maxWidth: '400px', margin: '0 auto' }}>
      <h2>{isLogin ? 'Login' : 'Signup'}</h2>
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={form.email}
        onChange={handleChange}
        style={{ display: 'block', marginBottom: '10px', width: '100%' }}
      />
      <input
        type="password"
        name="password"
        placeholder="Password"
        value={form.password}
        onChange={handleChange}
        style={{ display: 'block', marginBottom: '10px', width: '100%' }}
      />
      {isLogin ? (
        <button onClick={handleLogin}>Login</button>
      ) : (
        <button onClick={handleSignup}>Signup</button>
      )}
      <p style={{ marginTop: '10px', color: 'blue' }}>{message}</p>
      <p>
        {isLogin ? 'Don’t have an account?' : 'Already have an account?'}{' '}
        <button onClick={() => setIsLogin(!isLogin)} style={{ color: 'blue', background: 'none', border: 'none' }}>
          {isLogin ? 'Sign up' : 'Log in'}
        </button>
      </p>
    </div>
  );
};

export default SignupLogin;
