import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Import the Auth context

function Navbar() {
  const { isAuthenticated } = useAuth(); // Get the authentication state

  // Define navigation items
  const navItems = [
    { path: '/signup', label: 'Signup', requiresAuth: false },
    { path: '/about', label: 'About', requiresAuth: false },
    { path: '/product', label: 'Product', requiresAuth: false },
    { path: '/pricing', label: 'Pricing', requiresAuth: false },
    { path: '/trade', label: 'Trade', requiresAuth: true },
    { path: '/support', label: 'Support', requiresAuth: true },
  ];

  return (
    <nav className="navbar navbar-expand-lg border-bottom" style={{ backgroundColor: "#FFF" }}>
      <div className="container p-2">
        <Link className="navbar-brand" to="/">
          <img src="media/images/image.jpg" style={{ width: "25%" }} alt="Logo" />
        </Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mb-lg-0">
            {navItems.map((item, index) => {
              // Render item only if it doesn't require authentication or if the user is authenticated
              if (!item.requiresAuth || (item.requiresAuth && isAuthenticated)) {
                return (
                  <li className="nav-item" key={index}>
                    <Link className="nav-link active" to={item.path}>
                      {item.label}
                    </Link>
                  </li>
                );
              }
              return null; // Do not render anything if the condition is not met
            })}
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
