import React from 'react';

// Single Tourism Support Topic Component
function TourismSupportCard({ icon, title, links }) {
  return (
    <div className="col-md-4 p-4 mb-4" style={{ border: '1px solid #ddd', borderRadius: '10px', minHeight: '250px' }}>
      <h4>
        <i className={`fa ${icon} me-2`} aria-hidden="true"></i> {title}
      </h4>
      <ul style={{ paddingLeft: '20px', marginTop: '15px' }}>
        {links.map((link, index) => (
          <li key={index} style={{ marginBottom: '10px' }}>
            <a href={link.href} style={{ textDecoration: 'none', color: '#007bff' }}>
              {link.text}
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

// Main Tourism Support Page
function TourismSupport() {
  const tourismTopics = [
    {
      icon: 'fa-map-marker',
      title: 'Popular Destinations in Rajasthan',
      links: [
        { text: 'Jaipur City Palace', href: '#' },
        { text: 'Amber Fort, Jaipur', href: '#' },
        { text: 'Jaisalmer Fort', href: '#' },
        { text: 'Udaipur Lake Palace', href: '#' },
        { text: 'Pushkar Camel Fair', href: '#' },
        { text: 'Chittorgarh Fort', href: '#' },
        { text: 'Bikaner Junagarh Fort', href: '#' },
      ],
    },
    {
      icon: 'fa-bed',
      title: 'Accommodation',
      links: [
        { text: 'Hotels & Resorts', href: '#' },
        { text: 'Heritage Havelis', href: '#' },
        { text: 'Luxury Stays', href: '#' },
        { text: 'Budget Stays', href: '#' },
      ],
    },
    {
      icon: 'fa-road',
      title: 'Travel & Transport',
      links: [
        { text: 'Flights Booking', href: '#' },
        { text: 'Train Reservations', href: '#' },
        { text: 'Bus Services', href: '#' },
        { text: 'Local Cab & Rentals', href: '#' },
      ],
    },
    {
      icon: 'fa-lightbulb-o',
      title: 'Travel Tips',
      links: [
        { text: 'Best Travel Seasons', href: '#' },
        { text: 'Safety Guidelines', href: '#' },
        { text: 'Packing Essentials', href: '#' },
        { text: 'Cultural Etiquette', href: '#' },
      ],
    },
    {
      icon: 'fa-suitcase',
      title: 'Tour Packages',
      links: [
        { text: 'Adventure Tours', href: '#' },
        { text: 'Family Packages', href: '#' },
        { text: 'Couple Packages', href: '#' },
        { text: 'Weekend Getaways', href: '#' },
      ],
    },
    {
      icon: 'fa-globe',
      title: 'International Travel',
      links: [
        { text: 'Visa Assistance', href: '#' },
        { text: 'Flight Deals', href: '#' },
        { text: 'Travel Insurance', href: '#' },
        { text: 'Currency Exchange', href: '#' },
      ],
    },
  ];

  return (
    <div className="container">
      <div className="row p-5 mt-5 mb-5">
        <h1 className="fs-2 mb-5 text-center">
          Rajasthan Tourism Support Portal
        </h1>
        {tourismTopics.map((topic, index) => (
          <TourismSupportCard
            key={index}
            icon={topic.icon}
            title={topic.title}
            links={topic.links}
          />
        ))}
      </div>
    </div>
  );
}

export default TourismSupport;
