import React, { useState } from 'react';

const LoginPage = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });

  const handleInputChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      alert(`Logging in with\nEmail: ${formData.email}\nPassword: ${formData.password}`);
    } else {
      alert(`Signing up with\nName: ${formData.name}\nEmail: ${formData.email}\nPassword: ${formData.password}`);
    }
  };

  const toggleForm = () => {
    setIsLogin((prev) => !prev);
    setFormData({
      name: '',
      email: '',
      password: '',
    });
  };

  return (
    <>
      <style>{`
        * {
          box-sizing: border-box;
        }
        body {
          margin: 0;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          background: linear-gradient(135deg, #667eea, #764ba2);
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          color: #fff;
        }
        .container {
          background: rgba(255, 255, 255, 0.1);
          backdrop-filter: blur(10px);
          border-radius: 15px;
          padding: 40px 50px;
          box-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
          width: 350px;
          max-width: 90vw;
          position: relative;
          overflow: hidden;
        }
        h2 {
          margin-bottom: 20px;
          text-align: center;
          font-weight: 700;
          letter-spacing: 2px;
        }
        form {
          display: flex;
          flex-direction: column;
        }
        input {
          padding: 12px 15px;
          margin-bottom: 20px;
          border: none;
          border-radius: 8px;
          font-size: 16px;
          transition: background-color 0.3s ease, color 0.3s ease;
        }
        input::placeholder {
          color: #999;
        }
        input:focus {
          outline: none;
          background-color: #eee;
          color: #333;
          box-shadow: 0 0 8px #764ba2;
        }
        button.submit-btn {
          padding: 12px 15px;
          background: #764ba2;
          border: none;
          border-radius: 8px;
          color: white;
          font-weight: 700;
          font-size: 16px;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }
        button.submit-btn:hover {
          background: #5a3481;
        }
        .toggle-text {
          margin-top: 15px;
          text-align: center;
          font-size: 14px;
          cursor: pointer;
          color: #ccc;
          user-select: none;
          transition: color 0.3s ease;
        }
        .toggle-text:hover {
          color: #fff;
          text-decoration: underline;
        }
        /* Animation container for form switch */
        .form-slide {
          transition: transform 0.5s ease, opacity 0.5s ease;
        }
        .slide-in {
          transform: translateX(0);
          opacity: 1;
        }
        .slide-out-left {
          transform: translateX(-100%);
          opacity: 0;
          position: absolute;
          top: 40px;
          left: 0;
          right: 0;
        }
        .slide-out-right {
          transform: translateX(100%);
          opacity: 0;
          position: absolute;
          top: 40px;
          left: 0;
          right: 0;
        }
      `}</style>

      <div className="container" aria-live="polite">
        <h2>{isLogin ? 'Login to Your Account' : 'Create a New Account'}</h2>
        <div style={{position: 'relative', height: isLogin ? '220px' : '280px'}}>
          {/* Login Form */}
          <form
            className={`form-slide ${isLogin ? 'slide-in' : 'slide-out-left'}`}
            onSubmit={handleSubmit}
            aria-hidden={!isLogin}
            style={{pointerEvents: isLogin ? 'auto' : 'none'}}
          >
            <input
              type="email"
              name="email"
              placeholder="Email"
              required
              value={formData.email}
              onChange={handleInputChange}
              autoComplete="username"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              required
              value={formData.password}
              onChange={handleInputChange}
              autoComplete="current-password"
            />
            <button type="submit" className="submit-btn">Login</button>
          </form>

          {/* Signup Form */}
          <form
            className={`form-slide ${isLogin ? 'slide-out-right' : 'slide-in'}`}
            onSubmit={handleSubmit}
            aria-hidden={isLogin}
            style={{pointerEvents: isLogin ? 'none' : 'auto'}}
          >
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              required
              value={formData.name}
              onChange={handleInputChange}
              autoComplete="name"
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              required
              value={formData.email}
              onChange={handleInputChange}
              autoComplete="email"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              required
              value={formData.password}
              onChange={handleInputChange}
              autoComplete="new-password"
            />
            <button type="submit" className="submit-btn">Sign Up</button>
          </form>
        </div>

        <div
          className="toggle-text"
          onClick={toggleForm}
          tabIndex={0}
          onKeyDown={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              toggleForm();
            }
          }}
          role="button"
          aria-pressed={!isLogin}
        >
          {isLogin ? "Don't have an account? Sign Up" : 'Already have an account? Login'}
        </div>
      </div>
    </>
  );
};

export default LoginPage;
