import React, { useState, useEffect } from "react";

function Hero() {
  const [activeDetail, setActiveDetail] = useState(null);
  const [currentImage, setCurrentImage] = useState(0);

  // Hero slideshow images
  const images = [
    "https://images.unsplash.com/photo-1609940194206-8976f5f85c42?auto=format&fit=crop&w=1600&q=80", // Jaipur Palace
    "https://images.unsplash.com/photo-1548013146-72479768bada?auto=format&fit=crop&w=1600&q=80", // Goa Beach
    "https://images.unsplash.com/photo-1601265377804-0bc88dfb1ef0?auto=format&fit=crop&w=1600&q=80", // Kerala Backwaters
    "https://images.unsplash.com/photo-1623434096707-cdf5cb918416?auto=format&fit=crop&w=1600&q=80", // Ladakh
    "https://images.unsplash.com/photo-1589307004399-2e4b7da9a2fb?auto=format&fit=crop&w=1600&q=80" // Varanasi Ghat
  ];

  // Auto change background every 3 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % images.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [images.length]);

  const openDetail = (place) => setActiveDetail(place);
  const closeDetail = () => setActiveDetail(null);

  // Tourist places data
  const places = [
    {
      id: "jaipur",
      name: "Jaipur City Palace",
      location: "Jaipur, Rajasthan",
      weather: "Hot summers, pleasant winters",
      bestSeason: "Nov – Feb",
      disasters: "2019 locust attack",
      img: "https://images.unsplash.com/photo-1609940194206-8976f5f85c42?auto=format&fit=crop&w=900&q=80",
      imgLarge: "https://images.unsplash.com/photo-1609940194206-8976f5f85c42?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Royal vibes!", "⭐ 4 – Popular among tourists"],
      tagline: "Experience the royal architecture"
    },
    {
      id: "goa",
      name: "Goa Beaches",
      location: "Goa",
      weather: "Tropical, humid",
      bestSeason: "Nov – Feb",
      disasters: "2018 cyclone",
      img: "https://images.unsplash.com/photo-1548013146-72479768bada?auto=format&fit=crop&w=900&q=80",
      imgLarge: "https://images.unsplash.com/photo-1548013146-72479768bada?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Amazing sunsets", "⭐ 4 – Crowded at peak season"],
      tagline: "Perfect for parties and relaxation"
    },
    {
      id: "kerala",
      name: "Kerala Backwaters",
      location: "Alleppey, Kerala",
      weather: "Humid, tropical",
      bestSeason: "Sept – Mar",
      disasters: "2018 floods",
      img: "https://images.unsplash.com/photo-1601265377804-0bc88dfb1ef0?auto=format&fit=crop&w=900&q=80",
      imgLarge: "https://images.unsplash.com/photo-1601265377804-0bc88dfb1ef0?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Peaceful houseboats", "⭐ 4 – Humid climate"],
      tagline: "Serene greenery and backwaters"
    },
    {
      id: "ladakh",
      name: "Ladakh Mountains",
      location: "Jammu & Kashmir",
      weather: "Cold winters, mild summers",
      bestSeason: "Jun – Sep",
      disasters: "2010 cloudburst",
      img: "https://images.unsplash.com/photo-1623434096707-cdf5cb918416?auto=format&fit=crop&w=900&q=80",
      imgLarge: "https://images.unsplash.com/photo-1623434096707-cdf5cb918416?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Adventure paradise", "⭐ 4 – Remote and chilly"],
      tagline: "Breathtaking mountains and monasteries"
    },
    {
      id: "varanasi",
      name: "Varanasi Ghats",
      location: "Varanasi, Uttar Pradesh",
      weather: "Hot summers, foggy winters",
      bestSeason: "Oct – Mar",
      disasters: "2013 floods",
      img: "https://images.unsplash.com/photo-1589307004399-2e4b7da9a2fb?auto=format&fit=crop&w=900&q=80",
      imgLarge: "https://images.unsplash.com/photo-1589307004399-2e4b7da9a2fb?auto=format&fit=crop&w=1600&q=80",
      reviews: ["⭐ 5 – Spiritual and peaceful", "⭐ 4 – Crowded sometimes"],
      tagline: "Spiritual heart of India on Ganges"
    }
  ];

  return (
    <div>
      {/* Inline CSS */}
      <style>{`
        .modal-overlay {
          position: fixed;
          top: 0; left: 0;
          width: 100%; height: 100%;
          background: rgba(0,0,0,0.7);
          display: flex; align-items: center; justify-content: center;
          z-index: 1050;
        }
        .modal-content {
          background: #fff; padding: 20px;
          border-radius: 12px;
          max-width: 800px; width: 90%;
          max-height: 90%; overflow-y: auto;
        }
        .place-card:hover {
          transform: translateY(-4px);
          transition: transform 0.2s;
        }
        .place-card img {
          height: 200px;
          object-fit: cover;
          border-radius: 8px;
        }
      `}</style>

      {/* Hero Slideshow */}
      <section
        className="d-flex align-items-center justify-content-center text-white text-center"
        style={{
          backgroundImage: `url(${images[currentImage]})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "400px",
          transition: "background-image 1s ease-in-out"
        }}
      >
        <h1 className="p-3 rounded" style={{ background: "rgba(0,0,0,0.5)" }}>
          Travel Safe, Explore Freely
        </h1>
      </section>

      {/* Places Grid */}
      <main className="container my-5">
        <h2 className="mb-4 text-center">Top Tourist Places</h2>
        <div className="row g-4">
          {places.map((place) => (
            <div key={place.id} className="col-md-4">
              <div
                className="card shadow-sm place-card p-2"
                onClick={() => openDetail(place.id)}
                style={{ cursor: "pointer" }}
              >
                <img src={place.img} alt={place.name} />
                <div className="card-body">
                  <h5 className="card-title">{place.name}</h5>
                  <p className="text-muted">{place.location}</p>
                  <p>{place.tagline}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </main>

      {/* Detail Modal */}
      {activeDetail && (
        <div className="modal-overlay" onClick={closeDetail}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <button
              className="btn btn-danger mb-3"
              onClick={closeDetail}
              style={{ fontSize: "1.5rem", padding: "0.5rem 1rem" }}
            >
              Close ✖
            </button>
            {(() => {
              const place = places.find((p) => p.id === activeDetail);
              return (
                <>
                  <img
                    src={place.imgLarge}
                    alt={place.name}
                    className="img-fluid rounded mb-3"
                  />
                  <h2>{place.name}</h2>
                  <p><strong>Location:</strong> {place.location}</p>
                  <p><strong>Weather:</strong> {place.weather}</p>
                  <p><strong>Best Season:</strong> {place.bestSeason}</p>
                  <p><strong>Last Disasters:</strong> {place.disasters}</p>
                  <ul>
                    {place.reviews.map((r, i) => (
                      <li key={i}>{r}</li>
                    ))}
                  </ul>
                </>
              );
            })()}
          </div>
        </div>
      )}
    </div>
  );
}

export default Hero;
